mapper.select:
select

from ORGANIZATION WHERE AMPUTATED = 0 AND (NAME like #{name,jdbcType=VARCHAR} or ORGANIZATION_NO like #{name,jdbcType=VARCHAR})
ORDER BY DISPLAY_ORDER

SELECT ID, NAME, PARENT_ID, PATH, DISPLAY_ORDER, IS_SYNC_QUEUE, ORGANIZATION_NO FROM ( SELECT ORGANIZATION.ID, ORGANIZATION.NAME, ORGANIZATION.PARENT_ID, ORGANIZATION.DISPLAY_ORDER, ORGANIZATION.PATH, ORGANIZATION.IS_SYNC_QUEUE, ORGANIZATION.ORGANIZATION_NO FROM USER_ROLE INNER JOIN ROLE_ORGAN ON USER_ROLE.ROLE_ID = ROLE_ORGAN.ROLE_ID LEFT JOIN ORGANIZATION ON ORGANIZATION.id = ROLE_ORGAN.ORGAN_ID WHERE ORGANIZATION.AMPUTATED = 0 AND USER_ROLE.USER_ID = #{userId}
UNION ALL SELECT ORGANIZATION.ID, ORGANIZATION.NAME, ORGANIZATION.PARENT_ID, ORGANIZATION.DISPLAY_ORDER, ORGANIZATION.PATH, ORGANIZATION.IS_SYNC_QUEUE, ORGANIZATION.ORGANIZATION_NO FROM ORGANIZATION WHERE ORGANIZATION.id in (${path}) AND ORGANIZATION.AMPUTATED =0
UNION ALL SELECT ORGANIZATION.ID, ORGANIZATION.NAME, ORGANIZATION.PARENT_ID, ORGANIZATION.DISPLAY_ORDER, ORGANIZATION.PATH, ORGANIZATION.IS_SYNC_QUEUE, ORGANIZATION.ORGANIZATION_NO FROM ORGANIZATION WHERE ORGANIZATION.LINKED_ID =#{linkedId} AND ORGANIZATION.AMPUTATED =0
) GROUP BY ID, NAME, PARENT_ID, DISPLAY_ORDER, PATH, IS_SYNC_QUEUE, ORGANIZATION_NO
having (NAME like '%'||#{name}||'%' OR ORGANIZATION_NO LIKE '%'||#{name}||'%')
ORDER BY DISPLAY_ORDER

SELECT ID, NAME, PARENT_ID, DISPLAY_ORDER FROM ( SELECT ORGANIZATION.ID, ORGANIZATION.NAME, ORGANIZATION.PARENT_ID, ORGANIZATION.DISPLAY_ORDER FROM USER_ROLE INNER JOIN ROLE_ORGAN ON USER_ROLE.ROLE_ID = ROLE_ORGAN.ROLE_ID LEFT JOIN ORGANIZATION ON ORGANIZATION.id = ROLE_ORGAN.ORGAN_ID WHERE ORGANIZATION.AMPUTATED = 0 AND USER_ROLE.USER_ID = #{userId}
UNION ALL SELECT ORGANIZATION.ID, ORGANIZATION.NAME, ORGANIZATION.PARENT_ID, ORGANIZATION.DISPLAY_ORDER FROM ORGANIZATION WHERE ORGANIZATION.id in (${path}) AND ORGANIZATION.AMPUTATED =0
UNION ALL SELECT ORGANIZATION.ID, ORGANIZATION.NAME, ORGANIZATION.PARENT_ID, ORGANIZATION.DISPLAY_ORDER FROM ORGANIZATION WHERE ORGANIZATION.LINKED_ID =#{linkedId} AND ORGANIZATION.AMPUTATED =0
) GROUP BY ID, NAME, PARENT_ID, DISPLAY_ORDER ORDER BY DISPLAY_ORDER

select

from ORGANIZATION WHERE AMPUTATED = 0 and
NAME = #{name,jdbcType=VARCHAR}

