and ID!=#{orgId} and LINKED_ID !=#{id}
and ORGANIZATION_NO = #{organizationNo,jdbcType=VARCHAR}

select

from ORGANIZATION WHERE  AMPUTATED = 0
and NAME = #{organizationName,jdbcType=VARCHAR}

