mapper.update:
update ROLE_ACTION
set ACTION_ID = #{actionId,jdbcType=DECIMAL},
  MENU_ID = #{menuId,jdbcType=DECIMAL},
  ROLE_ID = #{roleId,jdbcType=DECIMAL}
where ID = #{id,jdbcType=DECIMAL}

mapper.select:
select temp.* from ( select m.id, m.name, m.parent_id, m.dis_play_order_by, m.has_child, ROW_NUMBER() OVER ( PARTITION BY m.ID ORDER BY m.id DESC ) AS row_flg from role_menu rm inner join menu m on m.id = rm.menu_id where rm.role_id in
#{item}
) temp where row_flg = '1'
